package Day2_151229;

public class OperatorEx7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean power = false;
		System.out.println(power);
		power = !power; // power의 값이 false에서 true로 바뀐다.
		System.out.println(power);
		power = !power; // power의 값이 true에서 false로 바뀐다.
		System.out.println(power);
	}

}
